    let navItems = [
        { name: 'Services', anchor: '/#services' },
        { name: 'About Us', anchor: '/#about' },
        { name: 'Free Mini Trainings', anchor: '/#trainings' },
        { name: 'Resources', anchor: 'https://resources.sparkstrategies.co/' },
        { name: 'Media Library', anchor: '/media-library' },
        { name: 'Blog', anchor: '/blog' },
    ];
    let moreItems = [
        // { name: 'Resources', anchor: 'https://resources.sparkstrategies.co/' },
        { name: 'Media Library', anchor: '/media-library' },
        { name: 'Blog', anchor: '/blog' },
    ];

    let navOpen = false;
    let moreOpen = false;

    let banner = true;

    function closeBanner() {
        banner = !banner;
    }

    let offset = 0;
    let tolerance = 0;
    let headerClass = 'bg-sparkHeader';
    let y = 0;
    let lastY = 0;

    function toggleNav() {
        navOpen = !navOpen;
    }

    function deriveClass(y, dy) {
        // show if at the top of page
        if (y < offset) {
            return 'bg-sparkHeader';
        }

        if (y === 0) {
            return 'bg-sparkHeader';
        }

        // don't change the state unless scroll delta
        // is above a threshold
        if (Math.abs(dy) <= tolerance) {
            return headerClass;
        }

        // if scrolling up, show
        if (dy < 0) {
            return 'bg-sparkHeader shadow';
        }

        // if scrolling down, hide
        return 'bg-sparkHeader shadow';
    }

    function updateClass(y) {
        const dy = lastY - y;
        lastY = y;
        return deriveClass(y, dy);
    }
    $: headerClass = updateClass(y);
</script>

