<style>
    .nav-trigger {
        position: relative;
        display: block;
        z-index: 4;
        width: 44px;
        height: 44px;
        overflow: hidden;
        color: transparent;
        white-space: nowrap;
        text-indent: 100%;
        cursor: pointer;
    }
    .bg-primaryColor .nav-trigger span,
    .bg-primaryColor .nav-trigger span::before,
    .bg-primaryColor .nav-trigger span::after {
        position: absolute;
        width: 32px;
        height: 2.25px;
        background: #fbf9f8;
        border-radius: 9999px;
    }
    .bg-sparkHeader .nav-trigger span,
    .bg-sparkHeader .nav-trigger span::before,
    .bg-sparkHeader .nav-trigger span::after {
        position: absolute;
        width: 32px;
        height: 2.25px;
        background: #fbf9f8;
        border-radius: 9999px;
    }

    .nav-trigger span {
        top: calc(50% - 2px);
        left: calc(50% - 18px);
        transition: background 0.3s;
    }

    .nav-trigger span::before,
    .nav-trigger span::after {
        content: '';
        top: 0;
        left: 0;
        transition:
            background 0.3s,
            transform 0.3s;
    }

    .nav-trigger span::before {
        transform: translateY(-8px);
    }

    .nav-trigger span::after {
        transform: translateY(8px);
    }

    .navOpen .nav-trigger span {
        transform: rotate(45deg);
    }

    .navOpen .nav-trigger span::before {
        transform: rotate(-92deg);
    }

    .navOpen .nav-trigger span::after {
        background: transparent;
    }
</style>
